import React, { Component } from 'react';
import { Route, withRouter } from 'react-router-dom';
import App from "./App";
import ItSidebar from './components/it-dept/it-sidebar';
import Login from "./components/login";
import EmpSidebar from './container/emp/emp_sidebar';

class AppWrapper extends Component {
	componentDidUpdate(prevProps) {
		if (this.props.location !== prevProps.location) {
			window.scrollTo(0, 0)
		}
	}

	render() {
		let x = 0;

		switch (this.props.location.pathname) {

			case "/":
				return <Route exact path="/" component={Login} />
			case "/ItSidebar":
				x = 1;
				return <Route exact path="/ItSidebar" component={ItSidebar} />
			case "/EmpSidebar":
				x = 2;
				return <Route exact path="/EmpSidebar" component={EmpSidebar} />
			default:
				if (x == 1) {
					<ItSidebar />
				}
				if (x == 2) {
					<EmpSidebar />
				}
		}
	}
}

export default withRouter(AppWrapper);
