import "./../../css/style.css";
import "./../../css/bootstrap.min.css";
import { Button } from "primereact/button";
import Jumbotron from "react-bootstrap/esm/Jumbotron";
import { Chart } from "primereact/chart";
import axios from "axios";
import constants from "../../utilities/constants";
import { useEffect, useState } from "react";

const constant = constants.getConstant();

var forName = [];
var forAmount = [];

function Graph() {
  const [data, setData] = useState([]);
  const [showLoading, setShowLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(constant.logList);
      setData(result.data.logistics);
      setShowLoading(false);
    };

    fetchData();
    
  }, []);

  

  const [basicData] = useState({

    labels: forName,
    datasets: [
      {
        label: "Vendor Name x Amount",
        backgroundColor: "#42A5F5",
        data: forAmount,
      },
    ],
  });

  const getLightTheme = () => {
    let basicOptions = {
      maintainAspectRatio: false,
      aspectRatio: 0.8,
      plugins: {
        legend: {
          labels: {
            color: "#495057",
          },
        },
      },
      scales: {
        x: {
          ticks: {
            color: "#495057",
          },
          grid: {
            color: "#ebedef",
          },
        },
        y: {
          ticks: {
            color: "#495057",
          },
          grid: {
            color: "#ebedef",
          },
        },
      },
    };
    return {
      basicOptions,
    };
  };

  const { basicOptions } = getLightTheme();

  const [chartData] = useState({
    labels: [forName[0], 'Drinking', 'Sleeping', 'Designing', 'Coding', 'Cycling', 'Running'],
    datasets: [
        {
            label: 'My First dataset',
            backgroundColor: 'rgba(179,181,198,0.2)',
            borderColor: 'rgba(179,181,198,1)',
            pointBackgroundColor: 'rgba(179,181,198,1)',
            pointBorderColor: '#fff',
            pointHoverBackgroundColor: '#fff',
            pointHoverBorderColor: 'rgba(179,181,198,1)',
            data: [65, 59, 90, 81, 56, 55, 40]
        },
        {
            label: 'My Second dataset',
            backgroundColor: 'rgba(255,99,132,0.2)',
            borderColor: 'rgba(255,99,132,1)',
            pointBackgroundColor: 'rgba(255,99,132,1)',
            pointBorderColor: '#fff',
            pointHoverBackgroundColor: '#fff',
            pointHoverBorderColor: 'rgba(255,99,132,1)',
            data: [28, 48, 40, 19, 96, 27, 100]
        }
    ]
});

const [lightOptions] = useState({
    plugins: {
        legend: {
            labels: {
                color: '#495057'
            }
        }
    },
    scales: {
        r: {
            pointLabels: {
                color: '#495057',
            },
            grid: {
                color: '#ebedef',
            },
            angleLines: {
                color: '#ebedef'
            }
        }
    }
});

  return (
    (
      <div>
        {data.map(
          (item, i) => (
            (forName[i] = item.name),
            (forAmount[i] = item.amount),
            (<span></span>)
          )
        )}
        <h2 className="mb-4">
          <b>Reports</b>
        </h2>
        <Button style={{ marginLeft: "2rem" }}>Logistics</Button>
        <Button style={{ marginLeft: "2rem" }}>Seed Batch</Button>
        <Button style={{ marginLeft: "2rem" }}>Purity</Button>
        <br></br>
        <br></br>

        <Jumbotron>
          <div className="card">
            {forName != undefined ? (
              <Chart type="bar" data={basicData} options={basicOptions} />
            ) : (
              <span></span>
            )}
          </div>
          <div className="card">
            {forName != undefined ? (
              <Chart type="radar" data={chartData} options={lightOptions} style={{ position: 'relative', width: '40%' }} />
            ) : (
              <span></span>
            )}
          </div>
        </Jumbotron>
        <Button style={{ marginLeft: "2rem" }}>Bar Graph</Button>
        <Button style={{ marginLeft: "2rem" }}>PolarArea Graph</Button>
      </div>
    )
  );
}

export default Graph;
