import React, { useEffect, useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { Calendar } from 'primereact/calendar';
import { Dialog } from 'primereact/dialog';
import { classNames } from 'primereact/utils';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import axios from 'axios';
import '../../css/style.css';
import { withRouter } from 'react-router-dom';
import constants from '../../utilities/constants';
const constant = constants.getConstant();
export const AddLogs = (props) => {
    // const [countries, setCountries] = useState([]);
    const [showMessage, setShowMessage] = useState(false);
    const [setShowData, setFormData] = useState({});
    const [selectedEmp, setSelectedEmp] = useState(null);
    const [data, setData] = useState([]);
    // const countryservice = new CountryService();
    const defaultValues = {
        name: '',
        description: '',
       // status: constant.Pen_status,
        assign_date: '',
        deadline: '',
        emp_id:''
    }
    useEffect(() => {
        // countryservice.getCountries().then(data => setCountries(data));
    }, []); // eslint-disable-line react-hooks/exhaustive-deps

    const { control, formState: { errors }, handleSubmit, reset } = useForm({ defaultValues });

    const onSubmit = (data) => {
        setFormData(data);

        axios.post(constant.logList, data)
            .then((result) => {
                setShowMessage(true)
            }).catch((error) => setShowMessage(false));
        reset();
    };

    const TaskList = () => {
        props.history.push({
            pathname: '/tasks/',
        });
    }
    const getFormErrorMessage = (name) => {
        return errors[name] && <small className="p-error">{errors[name].message}</small>
    };

    const dialogFooter = <div className="flex justify-content-center">
        <Button label="OK" className="p-button-text" autoFocus onClick={() => TaskList()} /></div>;
    
    const paginatorLeft = <Button type="button" icon="pi pi-refresh" className="p-button-text" />;
    const paginatorRight = <Button type="button" icon="pi pi-cloud" className="p-button-text" />;
    return (
        <div className="form-demo">
            <Dialog visible={showMessage} onHide={() => setShowMessage(false)} position="top" footer={dialogFooter} showHeader={false} breakpoints={{ '960px': '80vw' }} style={{ width: '30vw' }}>
                <div className="flex justify-content-center flex-column pt-6 px-3">
                    <i className="pi pi-check-circle" style={{ fontSize: '5rem', color: 'var(--green-500)' }} ></i>
                    <h5>Submission Successful!</h5>
                    <p style={{ lineHeight: 1.5, textIndent: '1rem' }}>
                       Task Details successfully added!
                    </p>
                </div>
            </Dialog>

            <div className="justify-content-center">
                <div className="card">
                    <h5 className="text-center">Task Details Submission</h5>
                    <form onSubmit={handleSubmit(onSubmit)} className="grid p-fluid">
                        <div className="field col-6">
                            <span className="p-float-label">
                                <Controller name="name" control={control} rules={{ required: 'Name is required.' }} render={({ field, fieldState }) => (
                                    <InputText id={field.name} {...field} autoFocus className={classNames({ 'p-invalid': fieldState.invalid })} />
                                )} />
                                <label htmlFor="name" className={classNames({ 'p-error': errors.name })}>Task Name*</label>
                            </span>
                            {getFormErrorMessage('name')}
                        </div><br></br>
                        <div className="field col-6">
                            <span className="p-float-label">
                                <Controller name="description" control={control} rules={{ required: 'Description is required.' }} render={({ field, fieldState }) => (
                                    <InputText id={field.description} {...field} autoFocus className={classNames({ 'p-invalid': fieldState.invalid })} />
                                )} />
                                <label htmlFor="description" className={classNames({ 'p-error': errors.description })}>Task description*</label>
                            </span>
                            {getFormErrorMessage('description')}
                        </div><br></br>
                        <div className="field col-6">
                            <span className="p-float-label">
                                <Controller name="description" control={control} rules={{ required: 'Selection of Employee is required.' }} render={({ field, fieldState }) => (
                                    <InputText id={field.description} {...field} autoFocus className={classNames({ 'p-invalid': fieldState.invalid })} />
                                )} />
                                <label htmlFor="description" className={classNames({ 'p-error': errors.description })}>Assign Task To*</label>
                            </span>
                            {getFormErrorMessage('description')}
                        </div><br></br>
                        <div className="field col-6">
                            <span className="p-float-label">
                                <Controller name="assign_date" control={control} render={({ field }) => (
                                    <Calendar id={field.assign_date} value={field.value} onChange={(e) => field.onChange(e.value)} dateFormat="dd/mm/yy" mask="99/99/9999" showIcon />
                                )} />
                                <label htmlFor="assign_date">Assigning Date</label>
                            </span>
                        </div><br></br>
                        <div className="field col-6">
                            <span className="p-float-label">
                                <Controller name="deadline" control={control} render={({ field }) => (
                                    <Calendar id={field.deadline} value={field.value} onChange={(e) => field.onChange(e.value)} dateFormat="dd/mm/yy" mask="99/99/9999" showIcon />
                                )} />
                                <label htmlFor="deadline">Deadline Date</label>
                            </span>
                        </div><br></br>
                        <div className='col-12'>
                        <h5 className="text-center">select employee</h5>
                        <DataTable value={data} selectionMode="single" selection={selectedEmp} onSelectionChange={e => setSelectedEmp(e.value)} dataKey="id"  paginator responsiveLayout="scroll"
                    paginatorTemplate="CurrentPageReport FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink RowsPerPageDropdown"
                    currentPageReportTemplate="Showing {first} to {last} of {totalRecords}" rows={10} rowsPerPageOptions={[10,20,50]}
                    paginatorLeft={paginatorLeft} paginatorRight={paginatorRight}>
                    <Column field="code" header="Code"></Column>
                    <Column field="name" header="Name"></Column>
                    <Column field="category" header="Category"></Column>
                    <Column field="quantity" header="Quantity"></Column>
                </DataTable></div>
                        <div className='col-12'>
                        <Button type="submit" label="Submit" className="mt-2" />
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default withRouter(AddLogs);
