import "./../../css/style.css";
import "./../../css/bootstrap.min.css";
import { Button } from "primereact/button";
import Jumbotron from "react-bootstrap/esm/Jumbotron";
import { Chart } from "primereact/chart";
import axios from "axios";
import constants from "../../utilities/constants";
import { useEffect, useState } from "react";

const constant = constants.getConstant();

var forName = [];
var forAmount = [];
let charname = "logistics"

function Graph() {
  const [data, setData] = useState([]);
  const [showLoading, setShowLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(constant.logList);
      setData(result.data.logistics);
      setShowLoading(false);
    };

    fetchData();
    
  }, []);

  const [chartData] = useState({
    datasets: [{
        data: forName,
        backgroundColor: [
            "#42A5F5"
        ],
        label: 'My dataset'
    }],
    labels: forAmount
});

const [lightOptions] = useState({
    plugins: {
        legend: {
            labels: {
                color: '#495057'
            }
        }
    },
    scales: {
        r: {
            grid: {
                color: '#ebedef'
            }
        }
    }
});

  const [basicData] = useState({

    labels: forName,
    datasets: [
      {
        label: "Vendor Name x Amount",
        backgroundColor: "#42A5F5",
        data: forAmount,
      },
    ],
  });

  const getLightTheme = () => {
    let basicOptions = {
      maintainAspectRatio: false,
      aspectRatio: 0.8,
      plugins: {
        legend: {
          labels: {
            color: "#495057",
          },
        },
      },
      scales: {
        x: {
          ticks: {
            color: "#495057",
          },
          grid: {
            color: "#ebedef",
          },
        },
        y: {
          ticks: {
            color: "#495057",
          },
          grid: {
            color: "#ebedef",
          },
        },
      },
    };
    return {
      basicOptions,
    };
  };

  const { basicOptions } = getLightTheme();

  return (
    (
      <div>
        {data.map(
          (item, i) => (
            (forName[i] = item.name),
            (forAmount[i] = item.amount),
            (<span></span>)
          )
        )}
        <h2 className="mb-4">
          <b>Reports</b>
        </h2>
        <Button style={{ marginLeft: "2rem" }}>Logistics</Button>
        <Button style={{ marginLeft: "2rem" }}>Seed Batch</Button>
        <Button style={{ marginLeft: "2rem" }}>Purity</Button>
        <br></br>
        <br></br>

        <Jumbotron>
          <div className="card">
            {forName != undefined ? (
              <Chart type="bar" data={basicData} options={basicOptions} />
            ) : (
              <span></span>
            )}
          </div>
        </Jumbotron>
        <Button style={{ marginLeft: "2rem" }}>Bar Graph</Button>
        <Button style={{ marginLeft: "2rem" }}>PolarArea Graph</Button>
      </div>
    )
  );
}

export default Graph;
