import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Spinner from 'react-bootstrap/Spinner';

import { withRouter ,useLocation} from 'react-router-dom';
import constants from '../../utilities/constants';


import Jumbotron from 'react-bootstrap/Jumbotron';
const constant = constants.getConstant();
function Show(props) {
  const [data, setData] = useState([]);
  const [showLoading, setShowLoading] = useState(true);
  
  const location = useLocation();
 
  const options = {
    method: 'GET',
    url: constant.empView,
    params: {  id : location.id },
    headers: {
        'content-type': 'application/json',
        'X-RapidAPI-Key': 'your-rapidapi-key',
        'X-RapidAPI-Host': 'microsoft-translator-text.p.rapidapi.com',
    },
      
};
  useEffect(() => {
   
    const fetchData = async () => {
      const result = await axios.request(options);
      setData(result.data);
      setShowLoading(false);
    };

    fetchData();
    
  }, []);

  return (
    <div>
      {showLoading && <Spinner animation="border" role="status">
        <span className="sr-only">Loading...</span>
      </Spinner> }
      <h2>Task View</h2>
      <Jumbotron>   
  {data.map((item,i) => (
 <div key={i}>

   <h4><b>Name: </b>{item.name} </h4>  
  <h4><b>Task Description: </b>{item.description}</h4> 
  <h4><b>Task status: </b>{item.status}</h4> 
  <h4><b>Task Assign Date: </b>{item.assign_date}</h4> 
  <h4><b>Task Deadline Date: </b>{item.deadline}</h4> 
  <h4><b>Assigned Employee name: </b>{item.taskdetails[0].name}</h4> 
  <h4><b>Emplyoee Department: </b>{item.taskdetails[0].department}</h4> 
  <h4><b>Employee Designation: </b>{item.taskdetails[0].designation}</h4> 
   </div>  
    ))}
  </Jumbotron>
    </div>
  );
}

export default withRouter(Show);
