import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Spinner from 'react-bootstrap/Spinner';
import { withRouter } from 'react-router-dom';
import Table from 'react-bootstrap/Table';
import { Button } from 'primereact/button';
import constants from '../../utilities/constants';
import Image from "../assets/mapsicon.png";

const constant = constants.getConstant();


function LogList(props) {

  const [data, setData] = useState([]);
  const [showLoading, setShowLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(constant.logList);
      setData(result.data.logistics);
      setShowLoading(false);
    };

    fetchData();
  }, []);

  const AddLogistics = () => {
    props.history.push({
      pathname: '/add_logs/',
    });
  }

  const EditLogs = (item) => {
    let Logs_Edit = {

      logs: Object.assign({}, item),

    }

    props.history.push({
      pathname: '/edit_logs/',
      data: Logs_Edit
    });
  }

  return (

    <div>
      <h2><b>Logistics Data List</b></h2>
      <div>
        <br></br><p>
        <Button style={{ fontSize:'18px'}} onClick={() => { AddLogistics() }}>Add Logistics</Button>
        </p>
      </div>
      {showLoading && <Spinner animation="border" role="status">
        <span className="sr-only">Loading...</span>
      </Spinner>}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th scope="col" style={{ fontSize:'18px'}}>#</th>
            <th scope="col" style={{ fontSize:'18px'}}>Vendor Name</th>
            <th scope="col" style={{ fontSize:'18px'}}>Amount</th>
            <th scope="col" style={{ fontSize:'18px'}}>Delivery Date</th>
            <th scope="col" style={{ fontSize:'18px'}}>Location</th>
            <th scope="col" style={{ fontSize:'18px'}}>Delivery Status</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, i) => (
              <tr style={ item.delivery_status === "pending" ? {backgroundColor: "#FFCCCB"} : {backgroundColor: "#90EE90"}} key={i}> 
                <th style={{ fontSize:'18px'}} scope="row" >{i + 1}</th>
                <td style={{ fontSize:'18px'}}>{item.name}</td>
                <td style={{ fontSize:'18px'}}>{item.amount}</td>
                <td style={{ fontSize:'18px'}}>{item.delivery_date.replace(/T.*/,'').split('-').reverse().join('-')}</td>
                <td style={{maxWidth: '50px'}}>
                  <a style={{color: "blue",fontSize:'18px'}} target='_blank' rel='noreferrer' href={item.location}>
                    <u>go to maps</u>
                  </a>
                </td>
                <td style={{ fontSize:'18px'}}>{item.delivery_status} 
                <Button style={{float: 'right',fontSize:'16px'}} className="p-button-warning" onClick={() => { EditLogs(item) }}>Edit</Button></td>
              </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default withRouter(LogList);
