import React from 'react';
import { Route, useLocation } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./style.css"
import { Routes } from './routes/index.js';
import Dashboard from "./container/Dashboard";
import EmpSidebar from './container/emp/emp_sidebar';
import ItSidebar from './components/it-dept/it-sidebar';
import './css/style.css';

import "primereact/resources/themes/lara-light-indigo/theme.css";  //theme
import "primereact/resources/primereact.min.css";                  //core css
import "primeicons/primeicons.css";
function App(props) {

  const location = useLocation();

  // const checkDesign = location.data.forDesign;
  // const designed = checkDesign.designation;

  

  const onEmployee = () => {
    window.location = "#/empList";
  };

  const onLogOut = () => {
    window.location = "/";
  };

  const onLogistics = () => {
    window.location = "#/logistics";
  };

  return (

    <div className="wrapper d-flex align-items-stretch">
      {
        (location.data.forDesign.designation === "admin") ? <ItSidebar /> : <EmpSidebar/>
      }

      <div id="content" className="p-4 p-md-5">
        <Route path="/App" exact component={Dashboard} />
        <Routes />
      </div>

    </div>
  );
}

export default App;
