import "./../../css/style.css";
import "./../../css/bootstrap.min.css";
import { Button } from "primereact/button";
import Jumbotron from "react-bootstrap/esm/Jumbotron";
import { Chart } from "primereact/chart";
import axios from "axios";
import constants from "../../utilities/constants";
import { useEffect, useState } from "react";

const constant = constants.getConstant();

var forName = [];
var forAmount = [];

var forBatchName = [];
var forTotalPurity = [];
var forBatchAmount = [];

function Graph() {
  const [data, setData] = useState([]);
  const [seed, setseed] = useState([]);
  const [purt, setpurt] = useState([]);
  const [chart, setchart] = useState([]);
  const [showLoading, setShowLoading] = useState(true);

  let charname = "";

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(constant.logList);
      setData(result.data.logistics);
      setShowLoading(false);
    };
    const fetchPurityData = async () => {
      const result = await axios(constant.refineryList);
      setseed(result.data.purityList);
      setShowLoading(false);
    };

    fetchData();
    fetchPurityData();
  }, []);

  const onLogs = () => {
    charname = "logistics";
    setchart(charname);
  };

  const onSeedBatch = () => {
    charname = "seedbatch";
    setchart(charname);
  };
  const onPurity = () => {
    charname = "purity";
    setchart(charname);
  };

  const [basicData] = useState({
    labels: forName,
    datasets: [
      {
        label: "Vendor Name x Amount",
        backgroundColor: "#fc8c03",
        data: forAmount,
      },
    ],
  });

  const getLightTheme = () => {
    let basicOptions = {
      maintainAspectRatio: false,
      aspectRatio: 0.8,
      plugins: {
        legend: {
          labels: {
            color: "#495057",
          },
        },
      },
      scales: {
        x: {
          ticks: {
            color: "#495057",
          },
          grid: {
            color: "#ebedef",
          },
        },
        y: {
          ticks: {
            color: "#495057",
          },
          grid: {
            color: "#ebedef",
          },
        },
      },
    };
    return {
      basicOptions,
    };
  };

  const { basicOptions } = getLightTheme();

  const [basicData1] = useState({
    labels: forBatchName,
    datasets: [
      {
        label: "Batch Name x Total Purity Score",
        backgroundColor: "#b6fc03",
        data: forTotalPurity,
      },
    ],
  });

  const getLightTheme1 = () => {
    let basicOptions = {
      maintainAspectRatio: false,
      aspectRatio: 0.8,
      plugins: {
        legend: {
          labels: {
            color: "#495057",
          },
        },
      },
      scales: {
        x: {
          ticks: {
            color: "#495057",
          },
          grid: {
            color: "#ebedef",
          },
        },
        y: {
          ticks: {
            color: "#495057",
          },
          grid: {
            color: "#ebedef",
          },
        },
      },
    };
    return {
      basicOptions,
    };
  };

  const { basicOptions1 } = getLightTheme();

  const [basicData2] = useState({
    labels: forBatchName,
    datasets: [
      {
        label: "Batch Name x Amount",
        backgroundColor: "#9660db",
        data: forBatchAmount,
      },
    ],
  });

  const getLightTheme2 = () => {
    let basicOptions = {
      maintainAspectRatio: false,
      aspectRatio: 0.8,
      plugins: {
        legend: {
          labels: {
            color: "#495057",
          },
        },
      },
      scales: {
        x: {
          ticks: {
            color: "#495057",
          },
          grid: {
            color: "#ebedef",
          },
        },
        y: {
          ticks: {
            color: "#495057",
          },
          grid: {
            color: "#ebedef",
          },
        },
      },
    };
    return {
      basicOptions,
    };
  };

  const { basicOptions2 } = getLightTheme();

  const [chartData] = useState({
    labels: forName,
    datasets: [
      {
        data: forAmount,
        backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56","#FF6384", "#36A2EB", "#FFCE56"],
        hoverBackgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
      },
    ],
  });

  const [lightOptions] = useState({
    plugins: {
      legend: {
        labels: {
          color: "#495057",
        },
      },
    },
  });


  const [chartData1] = useState({
    labels: forBatchName,
    datasets: [
      {
        data: forBatchAmount,
        backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56","#FF6384", "#36A2EB", "#FFCE56"],
        hoverBackgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
      },
    ],
  });

  const [lightOptions1] = useState({
    plugins: {
      legend: {
        labels: {
          color: "#495057",
        },
      },
    },
  });

  
  const [chartData2] = useState({
    labels: forBatchName,
    datasets: [
      {
        data: forBatchAmount,
        backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56","#FF6384", "#36A2EB", "#FFCE56"],
        hoverBackgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
      },
    ],
  });

  const [lightOptions2] = useState({
    plugins: {
      legend: {
        labels: {
          color: "#495057",
        },
      },
    },
  });


  return (
    <div>
      {data.map(
        (item, i) => (
          (forName[i] = item.name),
          (forAmount[i] = item.amount),
          (<span></span>)
        )
      )}
      {seed.map(
        (item, i) => (
          (forBatchName[i] = item.name),
          (forBatchAmount[i] = item.amount),
          (forTotalPurity[i] = item.totalScore),
          (<span></span>)
        )
      )}
      <h2 className="mb-4">
        <b>Reports</b>
      </h2>
      <Button
        style={{ marginLeft: "2rem" }}
        onClick={() => {
          onLogs();
        }}
      >
        Logistics
      </Button>
      <Button
        style={{ marginLeft: "2rem" }}
        onClick={() => {
          onSeedBatch();
        }}
      >
        Seed Batch
      </Button>
      <Button
        style={{ marginLeft: "2rem" }}
        onClick={() => {
          onPurity();
        }}
      >
        Purity
      </Button>
      <br></br>
      <br></br>

      <Jumbotron>
        <div className="card">
          {chart === "logistics" ? (
            <Chart type="bar" data={basicData} options={basicOptions} />
          ) : chart === "seedbatch" ? (
            <Chart type="bar" data={basicData1} options={basicOptions1} />
          ) : chart === "purity" ? (
            <Chart type="bar" data={basicData2} options={basicOptions2} />
          ) : (
            <span></span>
          )}
        </div>
        <div className="card">
          {chart === "logistics" ? (
            <Chart type="doughnut" data={chartData} options={lightOptions} style={{ position: 'relative', width: '40%' }} />
          ) : chart === "seedbatch" ? (
            <Chart type="doughnut" data={chartData1} options={lightOptions1} style={{ position: 'relative', width: '40%' }} />
          ) : chart === "purity" ? (
            <Chart type="doughnut" data={chartData2} options={lightOptions2} style={{ position: 'relative', width: '40%' }} />
          ) : (
            <span></span>
          )}
        </div>
      </Jumbotron>
      <Button style={{ marginLeft: "2rem" }}>Bar Graph</Button>
      <Button style={{ marginLeft: "2rem" }}>PolarArea Graph</Button>
    </div>
  );
}

export default Graph;
