import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Spinner from 'react-bootstrap/Spinner';
import { withRouter } from 'react-router-dom';
import Table from 'react-bootstrap/Table';
import { Button } from 'primereact/button';
import constants from '../../utilities/constants';
import { parseISO } from "date-fns"
import { Dialog } from 'primereact/dialog';
import '../../css/style.css';
const constant = constants.getConstant();
function EmpList(props) {
  const [data, setData] = useState([]);
  const [workingID, setworkingID] = useState({});
  const [showLoading, setShowLoading] = useState(true);
  const [showMessage, setShowMessage] = useState(false);

  const fetchData = e => {
    const query = e.target.value
    fetch(constant.machineList + `?name=${query}`)
      .then(response => {
        return response.json()
      })
      .then(data => {
        setData(data.machineList)
      })
  }

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(constant.machineList);
      setData(result.data.machinesList);
      setShowLoading(false);
    };

    fetchData();
  }, []);

  const showDetail = (id) => {
    props.history.push({
      pathname: '/show_detail/',
      id: id

    });
  }
  const AddEmployee = () => {
    props.history.push({
      pathname: '/add_emp/',
    });
  }

  // const deleteData = () => {

  //   let id = deleteId;
  //   axios.delete(constant.empList + `?id=${id}`)
  //     .then((result) => {
  //       setShowMessage(false);
  //       axios.get(constant.empList)
  //         .then((result) => {
  //           setData(result.data.employees);

  //         }).catch((error) => setShowMessage(false));

  //     }).catch((error) => setShowMessage(false));

  // }
  //  const Working = () => {

  //   let id = workingID;
  //   axios.delete(constant.empList + `?id=${id}`)
  //     .then((result) => {
  //       setShowMessage(false);
  //       axios.get(constant.empList)
  //         .then((result) => {
  //           setData(result.data.employees);

  //         }).catch((error) => setShowMessage(false));

  //     }).catch((error) => setShowMessage(false));

  // }
  let empdata;
  useEffect(() => {
     empdata = JSON.parse(localStorage.getItem('data'));
    if (empdata) {
      setData(empdata);
    }
  }, []);
//   let emp_id;
//  { data.map((item) => (
//   emp_id = item._id,
//     <span></span>
//   ))}
  const onWorking = (id) => {
   
    axios.put(constant.machineWorking + `?id=${id}&emp_id=${empdata._id}`)
      .then((result) => {
        setShowMessage(false);
        axios.get(constant.machineWorking)
          .then((result) => {
            setData(result.data.machineWorking);

          }).catch((error) => setShowMessage(false));

      }).catch((error) => setShowMessage(false));
  }


  const EditEmployee = (item) => {
    item['date_of_birth'] = parseISO(item.date_of_birth);
    let Employee_Edit = {

      emp: Object.assign({}, item),

    }

    props.history.push({
      pathname: '/edit_emp/',
      data: Employee_Edit
    });
  }
  const selectedItem = (id) => {
    setworkingID(id);
    setShowMessage(true)
  }


  return (

    <div className="form-demo">
      
      <h2>Employee List</h2>
      <div>
        <input placeholder='Search Employee' onChange={fetchData} label="Search User" />
      </div>
      <div>
        <br></br><p>
          <Button onClick={() => { AddEmployee() }}>Add Employee</Button>
        </p>
      </div>
      {showLoading && <Spinner animation="border" role="status">
        <span className="sr-only">Loading...</span>
      </Spinner>}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th scope="col" style={{ fontSize:'20px'}}>#</th>
            <th scope="col" style={{ fontSize:'20px'}}>First Name</th>
            <th scope="col" style={{ fontSize:'20px'}}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, i) => (
            <tr key={i}>
              <th scope="row" >{i + 1}</th>
              <td style={{ fontSize:'20px'}}>{item.name}</td>
              <td>
                <Button onClick={() => { showDetail(item._id) }} className="p-button-success">
                  Working
                </Button>
                <Button style={{ marginLeft:'1rem'}} className="p-button-warning" onClick={() => { onWorking(item._id) }}>Under Maintenance</Button>
                <Button style={{ marginLeft:'1rem'}} className="p-button-danger" onClick={() => { selectedItem(item._id) }}>Required Maintenance</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default withRouter(EmpList);
