import React from "react";
import { Switch, Route, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import './../../css/style.css';

const onEmployee = () => {
  window.location = "#/empList";
};

function ItSidebar() {
  return (
    <div className="App">
      <div style={{height: '100vh', width: '20vw'}} className="d-flex flex-column flex-shrink-0 p-3 text-white bg-dark">
        <h3 style={{textAlign: 'center'}}>IT Department</h3>
        <hr />
        <ul className="nav nav-pills flex-column mb-auto">

          <div class="d-grid gap-2">
          <button type="button" class="btn btn-dark">Dashboard</button><br></br>

          <button type="button" class="btn btn-dark" onClick={() => { onEmployee() }}>Employee Data</button><br></br>
          </div>
        </ul>
        <hr />
        <strong style={{textAlign: 'center'}}>Admin</strong><br />
        <button type="button" className="btn btn-outline-danger">Sign out</button>
      </div>
      <nav id="sidebar">
        <div className="p-4 pt-5">
          <h3 style={{ textAlign: 'center' }}>Employee</h3>
   
          <hr />
          <ul className="nav nav-pills flex-column mb-auto">
        
            <button type="button" className="btn btn-dark" onClick={() => { onEmployee() }}>Employee</button><br></br>
            <button type="button" className="btn btn-dark" >Tasks</button><br></br>
            <button type="button" className="btn btn-dark">Order Status</button><br></br>
           
          </ul>

          <hr />

          <strong style={{textAlign: 'center'}}>Admin</strong><br />
          <button type="button" className="btn btn-outline-danger">Sign out</button>
          <div className="footer">
          </div>
        </div>
      </nav>
    </div>
  );
}

export default ItSidebar;
