import React, { useState, useEffect } from "react";
import axios from "axios";
import Spinner from "react-bootstrap/Spinner";
import { withRouter } from "react-router-dom";
import Table from "react-bootstrap/Table";
import { Button } from "primereact/button";
import constants from "../../utilities/constants";
import "../../css/style.css";

const constant = constants.getConstant();
const realdata = JSON.parse(localStorage.getItem("data"));
let forID;

function AttendanceList(props) {
  const [data, setData] = useState([]);
  const [deleteId, setDeleteId] = useState({});
  const [showLoading, setShowLoading] = useState(true);
  const [showMessage, setShowMessage] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(constant.attendList + `?id=${forID}`);
      setData(result.data.attendanceList);
      setShowLoading(false);
      console.log(result);
    };

    realdata.map((item) => (forID = item._id));

    fetchData();
  }, []);

  const onMark = (id) => {
   
    axios.put(constant.attendList + `?id=${id}&emp_id=${forID}`)
      .then((result) => {
        setShowMessage(false);
        axios.get(constant.attendList)
          .then((result) => {
            setData(result.data.attendList);
            console.log(result.data.attendList);
          }).catch((error) => setShowMessage(false));

      }).catch((error) => setShowMessage(false));
  }
  const showDetail = (id) => {
    props.history.push({
      pathname: "/attendanceDetails/",
      id: id,
    });
  };

  return (
    <div className="form-demo">
      <h2><b>Attendance List</b></h2>
      {data.map((item) =>
        item.designation === "supervisor" ? (
          <div>
            <br></br>

          </div>
        ) : (
          <span></span>
        )
      )}

      {showLoading && (
        <Spinner animation="border" role="status">
          <span className="sr-only">Loading...</span>
        </Spinner>
      )}

      <Table striped bordered hover>
        <thead>
          <tr>
            <th scope="col" style={{ fontSize: "20px" }}>
              #
            </th>
            <th scope="col" style={{ fontSize: "20px" }}>
              Name
            </th>
            <th scope="col" style={{ fontSize: "20px" }}>
              Designation
            </th>
            <th scope="col" style={{ fontSize: "20px" }}>
              Date
            </th>
            <th scope="col" style={{ fontSize: "20px" }}>
              Details
            </th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, i) => (
            <tr key={i}>
              <th scope="row">{i + 1}</th>
              <td style={{ fontSize: "17px" }}>
                {item.attendancedetails[0].name}
              </td>
              <td style={{ fontSize: "17px" }}>
                {item.attendancedetails[0].designation}
              </td>
              <td style={{ fontSize: "17px" }}>
                {item.date.replace(/T.*/, "").split("-").reverse().join("-")}
              </td>
              <td>
                <Button onClick={() => { showDetail(item._id); }} className="p-button-success" >View</Button>

                <Button style={{ marginLeft:'1rem'}} onClick={() => { onMark(item._id)}} > Mark Attendance </Button>

              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default withRouter(AttendanceList);
