import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Spinner from 'react-bootstrap/Spinner';
import { withRouter } from 'react-router-dom';
import Table from 'react-bootstrap/Table';
import { Button } from 'primereact/button';
import constants from '../../utilities/constants';
import { parseISO } from "date-fns"
import { Dialog } from 'primereact/dialog';
import '../../css/style.css';

const constant = constants.getConstant();
let forID;

function AttendanceList(props) {

  const [data, setData] = useState([]);
  const [deleteId, setDeleteId] = useState({});
  const [showLoading, setShowLoading] = useState(true);
  const [showMessage, setShowMessage] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(constant.attendList + `?id=${forID}`);
      setData(result.data.employees);
      setShowLoading(false);
    };

    const realdata = JSON.parse(localStorage.getItem("data"))
    realdata.map((item) => (forID = item._id))

    fetchData();
  }, []);

  const showDetail = (id) => {
    props.history.push({
      pathname: '/attendanceDetails/',
      id: id

    });
  }
  const MarkAttendance = () => {
    props.history.push({
      pathname: '/markAttendance/',
    });
  }

  const dialogFooter = <div className="flex justify-content-center">
    <Button label="Yes" className="p-button-danger" autoFocus onClick={() => deleteData()} />
    <Button label="No" className="p-button-warning" autoFocus onClick={() => setShowMessage(false)} />
  </div>;
  return (

    <div className="form-demo">
      <Dialog visible={showMessage} onHide={() => setShowMessage(false)} position="top" footer={dialogFooter} showHeader={false} breakpoints={{ '960px': '80vw' }} style={{ width: '30vw' }}>
        <div className="flex justify-content-center flex-column pt-6 px-3">

          <h5>Are you sure you want to Delete?</h5>
          <p style={{ lineHeight: 1.5, textIndent: '1rem' }}>
            Your Employee is Updated successfully
          </p>
        </div>
      </Dialog>
      <h2>Attendance List</h2>
      {showLoading && <Spinner animation="border" role="status">
        <span className="sr-only">Loading...</span>
      </Spinner>}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th scope="col" style={{ fontSize:'20px'}}>#</th>
            <th scope="col" style={{ fontSize:'20px'}}>Name</th>
            <th scope="col" style={{ fontSize:'20px'}}>Designation</th>
            <th scope="col" style={{ fontSize:'20px'}}>Department</th>
            <th scope="col" style={{ fontSize:'20px'}}>Attendance</th>
            <th scope="col" style={{ fontSize:'20px'}}>Details</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, i) => (
            <tr key={i}>
              <th scope="row" >{i + 1}</th>
              <td style={{ fontSize:'20px'}}>{item.name}</td>
              <td>
              <Button onClick={() => { showDetail(item._id) }} className="p-button-success">
                  View
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default withRouter(AttendanceList);
