import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Spinner from 'react-bootstrap/Spinner';
import { withRouter } from 'react-router-dom';
import Table from 'react-bootstrap/Table';
import { Button } from 'primereact/button';
import constants from '../../utilities/constants';
import { parseISO } from "date-fns"
import '../../css/style.css';
const constant = constants.getConstant();
function TaskList(props) {
  const [data, setData] = useState([]);
  
  const [showLoading, setShowLoading] = useState(true);
  const [showMessage, setShowMessage] = useState(false);

  const fetchData = e => {
    const query = e.target.value
    fetch(constant.taskList + `?name=${query}`)
      .then(response => {
        return response.json()
      })
      .then(data => {
        setData(data.taskList)
      })
  }

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(constant.taskList);
      setData(result.data.taskList);
      setShowLoading(false);
    };

    fetchData();
  }, []);

  const showDetail = (id) => {
    props.history.push({
      pathname: '/view_task/',
      id: id
    });
  }
  const AddTask = () => {
    props.history.push({
      pathname: '/add_task/',
    });
  }

  const EditEmployee = (item) => {
    item['date_of_birth'] = parseISO(item.date_of_birth);
    let Employee_Edit = {

      emp: Object.assign({}, item),

    }
  }

  return (

    <div className="form-demo">
    
      <h2>Task List</h2>
      <div>
        <input onChange={fetchData} label="Search User" />
      </div>
      <div>
        <br></br><p>
          <Button onClick={() => { AddTask() }}>Add Task</Button>
        </p>
      </div>
      {showLoading && <Spinner animation="border" role="status">
        <span className="sr-only">Loading...</span>
      </Spinner>}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Task Name</th>
            <th scope="col">Employee Name</th>
            <th scope="col">Employee Designation</th>
            <th scope="col">Employee Department</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, i) => (
            <tr key={i}>
              <th scope="row" >{i + 1}</th>
              <td >{item.name}</td>
              <td >{item.taskdetails[0].name}</td>
              <td >{item.taskdetails[0].designation}</td>
              <td >{item.taskdetails[0].department}</td>
              <td>
                <Button onClick={() => { showDetail(item._id) }} className="p-button-success">
                  View
                </Button>
                <Button className="p-button-warning" onClick={() => { EditEmployee(item) }}>Edit</Button>
               
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default withRouter(TaskList);
